import React, { useState, useEffect } from 'react';
import axios from 'axios';
import {
  Container,
  TextField,
  Button,
  Table,
  TableBody,
  TableCell,
  TableContainer,
  TableHead,
  TableRow,
  Select,
  MenuItem,
  InputLabel,
  FormControl,
  Typography,
  Paper,
} from '@mui/material';
import './App.css';

const App = () => {
  const [data, setData] = useState([]);
  const [newRecord, setNewRecord] = useState({ amount: '', actionName: '', actionType: '', Impact: 'Low' });

  useEffect(() => {
    fetchData();
  }, []);

  const fetchData = async () => {
    const result = await axios.get('http://localhost:3000/data');
    setData(result.data);
  };

  const handleInputChange = (index, field, value) => {
    const newData = [...data];
    newData[index][field] = value;
    setData(newData);
  };

  const handleSave = async () => {
    const promises = data.map((item) => axios.put(`http://localhost:3000/data/${item.id}`, item));
    await Promise.all(promises);
    fetchData();
  };

  const handleNewRecordChange = (field, value) => {
    setNewRecord({ ...newRecord, [field]: value });
  };

  const handleAddNewRecord = async () => {
    await axios.post('http://localhost:3001/data', newRecord);
    fetchData();
    setNewRecord({ amount: '', actionName: '', actionType: '', Impact: 'Low' });
  };

  const renderTable = () => {
    return (
      <TableContainer component={Paper} className="table-container">
        <Table>
          <TableHead>
            <TableRow>
              <TableCell>ID</TableCell>
              <TableCell>Amount</TableCell>
              <TableCell>Action Name</TableCell>
              <TableCell>Action Type</TableCell>
              <TableCell>Status</TableCell>
              <TableCell>Impact</TableCell>
            </TableRow>
          </TableHead>
          <TableBody>
            {data.map((item, index) => (
              <TableRow key={item.id}>
                <TableCell>{item.id}</TableCell>
                <TableCell>
                  <TextField
                    type="number"
                    value={item.amount}
                    onChange={(e) => handleInputChange(index, 'amount', e.target.value)}
                    fullWidth
                  />
                </TableCell>
                <TableCell>
                  <FormControl fullWidth>
                    <Select
                      value={item.actionName}
                      onChange={(e) => handleInputChange(index, 'actionName', e.target.value)}
                    >
                      <MenuItem value="Action1">Action1</MenuItem>
                      <MenuItem value="Action2">Action2</MenuItem>
                      <MenuItem value="Action3">Action3</MenuItem>
                    </Select>
                  </FormControl>
                </TableCell>
                <TableCell>
                  <FormControl fullWidth>
                    <Select
                      value={item.actionType}
                      onChange={(e) => handleInputChange(index, 'actionType', e.target.value)}
                    >
                      <MenuItem value="Type1">Type1</MenuItem>
                      <MenuItem value="Type2">Type2</MenuItem>
                      <MenuItem value="Type3">Type3</MenuItem>
                    </Select>
                  </FormControl>
                </TableCell>
                <TableCell>{item.status}</TableCell>
                <TableCell>{item.Impact}</TableCell>
              </TableRow>
            ))}
          </TableBody>
        </Table>
      </TableContainer>
    );
  };

  const renderForm = () => {
    return (
      <Paper className="form-container">
        <Typography variant="h6" className="form-title">Add New Record</Typography>
        <FormControl fullWidth margin="normal">
          <TextField
            type="number"
            label="Amount"
            value={newRecord.amount}
            onChange={(e) => handleNewRecordChange('amount', e.target.value)}
            fullWidth
          />
        </FormControl>
        <FormControl fullWidth margin="normal">
          <InputLabel>Action Name</InputLabel>
          <Select
            value={newRecord.actionName}
            onChange={(e) => handleNewRecordChange('actionName', e.target.value)}
            fullWidth
          >
            <MenuItem value="Action1">Action1</MenuItem>
            <MenuItem value="Action2">Action2</MenuItem>
            <MenuItem value="Action3">Action3</MenuItem>
          </Select>
        </FormControl>
        <FormControl fullWidth margin="normal">
          <InputLabel>Action Type</InputLabel>
          <Select
            value={newRecord.actionType}
            onChange={(e) => handleNewRecordChange('actionType', e.target.value)}
            fullWidth
          >
            <MenuItem value="Type1">Type1</MenuItem>
            <MenuItem value="Type2">Type2</MenuItem>
            <MenuItem value="Type3">Type3</MenuItem>
          </Select>
        </FormControl>
        <FormControl fullWidth margin="normal">
          <InputLabel>Impact</InputLabel>
          <Select
            value={newRecord.Impact}
            onChange={(e) => handleNewRecordChange('Impact', e.target.value)}
            fullWidth
          >
            <MenuItem value="Low">Low</MenuItem>
            <MenuItem value="Mid">Mid</MenuItem>
            <MenuItem value="High">High</MenuItem>
          </Select>
        </FormControl>
        <Button variant="contained" color="primary" onClick={handleAddNewRecord} fullWidth className="save-button">
          Add Record
        </Button>
      </Paper>
    );
  };

  return (
    <Container className="app-container">
      <Typography variant="h4" gutterBottom className="app-title">Data Management App</Typography>
      {renderTable()}
      <Button variant="contained" color="primary" onClick={handleSave} fullWidth className="save-button">
        Save
      </Button>
      <Typography variant="h6" gutterBottom>Add New Record</Typography>
      {renderForm()}
    </Container>
  );
};

export default App;
